class Solution:
    def minOperations(self, nums: List[int], x: int, k: int) -> int:
        """
        Returns the minimum number of operations needed to have at least k non-overlapping subarrays of size exactly x in nums, where all elements within each subarray are equal.
        """
        n = len(nums)
        if n < k * x:
            return -1

        prefix_sums = [0] * (n + 1)
        for i in range(n):
            prefix_sums[i + 1] = prefix_sums[i] + nums[i]

        min_operations = float('inf')
        for i in range(n - k * x + 1):
            target = prefix_sums[i + k * x] - prefix_sums[i]
            if target % x == 0:
                target = target // x
                left = i
                right = i + k * x - 1
                while left < right:
                    mid = (left + right) // 2
                    if prefix_sums[mid + 1] - prefix_sums[mid] == target:
                        right = mid
                    else:
                        left = mid + 1
                min_operations = min(min_operations, n - right - 1)

        return min_operations if min_operations != float('inf') else -1
