class Solution:
    def sumOfGoodNumbers(self, nums: List[int], k: int) -> int:
        """
        Returns the sum of all the good elements in the array.
        An element is considered good if it is strictly greater than the elements at indices i - k and i + k.
        If neither of these indices exists, the element is still considered good.
        """
        n = len(nums)
        prefix_sum = [0] * (n + 1)
        for i in range(n):
            prefix_sum[i + 1] = prefix_sum[i] + nums[i]

        total_sum = 0
        for i in range(n):
            left_index = max(0, i - k)
            right_index = min(n - 1, i + k)
            if left_index == 0 and right_index == n - 1:
                total_sum += nums[i]
            elif left_index == 0:
                total_sum += max(0, nums[i] - prefix_sum[right_index + 1])
            elif right_index == n - 1:
                total_sum += max(0, nums[i] - prefix_sum[left_index])
            else:
                total_sum += max(0, nums[i] - (prefix_sum[right_index + 1] - prefix_sum[left_index]))

        return total_sum
