class Solution:
    def lenOfVDiagonal(self, grid: List[List[int]]) -> int:
        """
        Returns the length of the longest V-shaped diagonal segment in the grid.
        If no valid segment exists, returns 0.
        """
        def is_valid_segment(x, y, direction):
            """
            Checks if the segment starting at (x, y) in the given direction is valid.
            """
            if direction == 0:  # top-left to bottom-right
                if y + 1 < len(grid) and grid[x][y + 1] == 2:
                    return True
            elif direction == 1:  # bottom-right to top-left
                if x + 1 < len(grid) and grid[x + 1][y] == 2:
                    return True
            elif direction == 2:  # top-right to bottom-left
                if y - 1 >= 0 and grid[x][y - 1] == 2:
                    return True
            elif direction == 3:  # bottom-left to top-right
                if x - 1 >= 0 and grid[x - 1][y] == 2:
                    return True
            return False

        def find_longest_segment(x, y, direction):
            """
            Finds the length of the longest V-shaped diagonal segment starting at (x, y)
            in the given direction.
            """
            length = 0
            while True:
                if x < 0 or y < 0 or x >= len(grid) or y >= len(grid[0]):
                    break
                if grid[x][y] == 0:
                    break
                if grid[x][y] == 1:
                    if is_valid_segment(x, y, direction):
                        length += 1
                        x += 1
                        y += 1
                    else:
                        break
                elif grid[x][y] == 2:
                    length += 1
                    x += 1
                    y += 1
                else:
                    break
            return length

        max_length = 0
        for i in range(len(grid)):
            for j in range(len(grid[0])):
                if grid[i][j] == 1:
                    max_length = max(max_length, find_longest_segment(i, j, 0))
                    max_length = max(max_length, find_longest_segment(i, j, 1))
                    max_length = max(max_length, find_longest_segment(i, j, 2))
                    max_length = max(max_length, find_longest_segment(i, j, 3))
        return max_length
