            if i < 0 or j < 0 or i >= len(grid) or j >= len(grid[0]) or grid[i][j] != 1:
                return 0

            if sequence == 0:
                sequence = 2
                direction = (direction + 1) % 4
            elif sequence == 1:
                sequence = 0
                direction = (direction + 1) % 4
            elif sequence == 2:
                sequence = 0
                direction = (direction + 1) % 4
            elif sequence == 3:
                sequence = 1
                direction = (direction + 1) % 4

            if direction == 0:
                if i + 1 < len(grid) and j + 1 < len(grid[0]) and grid[i + 1][j + 1] == 2:
                    return dfs(i + 1, j + 1, direction, sequence, length + 1)
                else:
                    return 0
            elif direction == 1:
                if i + 1 < len(grid) and j - 1 >= 0 and grid[i + 1][j - 1] == 2:
                    return dfs(i + 1, j - 1, direction, sequence, length + 1)
                else:
                    return 0
            elif direction == 2:
                if i - 1 >= 0 and j - 1 >= 0 and grid[i - 1][j - 1] == 2:
                    return dfs(i - 1, j - 1, direction, sequence, length + 1)
                else:
                    return 0
            elif direction == 3:
                if i - 1 >= 0 and j + 1 < len(grid[0]) and grid[i - 1][j + 1] == 2:
                    return dfs(i - 1, j + 1, direction, sequence, length + 1)
                else:
                    return 0

        max_length = 0
        for i in range(len(grid)):
            for j in range(len(grid[0])):
                if grid[i][j] == 1:
                    max_length = max(max_length, dfs(i, j, 0, 0, 1))
                    max_length = max(max_length, dfs(i, j, 1, 0, 1))
                    max_length = max(max_length, dfs(i, j, 2, 0, 1))
                    max_length = max(max_length, dfs(i, j, 3, 0, 1))

        return max_length
