class Solution:
    def lenOfVDiagonal(self, grid: List[List[int]]) -> int:
        def dfs(i, j, direction, count):
            if i < 0 or j < 0 or i >= len(grid) or j >= len(grid[0]) or grid[i][j] != 1:
                return count
            if direction == 0:  # top-left to bottom-right
                if i + 1 < len(grid) and j + 1 < len(grid[0]) and grid[i + 1][j + 1] == 2:
                    return dfs(i + 1, j + 1, 1, count + 1)
                else:
                    return dfs(i + 1, j, 0, count)
            elif direction == 1:  # bottom-right to top-left
                if i - 1 >= 0 and j - 1 >= 0 and grid[i - 1][j - 1] == 2:
                    return dfs(i - 1, j - 1, 2, count + 1)
                else:
                    return dfs(i, j - 1, 1, count)
            elif direction == 2:  # top-right to bottom-left
                if i + 1 < len(grid) and j - 1 >= 0 and grid[i + 1][j - 1] == 2:
                    return dfs(i + 1, j - 1, 3, count + 1)
                else:
                    return dfs(i, j + 1, 2, count)
            else:  # bottom-left to top-right
                if i - 1 >= 0 and j + 1 < len(grid[0]) and grid[i - 1][j + 1] == 2:
                    return dfs(i - 1, j + 1, 0, count + 1)
                else:
                    return dfs(i, j - 1, 3, count)

        max_length = 0
        for i in range(len(grid)):
            for j in range(len(grid[0])):
                if grid[i][j] == 1:
                    max_length = max(max_length, dfs(i, j, 0, 1))
                    max_length = max(max_length, dfs(i, j, 1, 1))
                    max_length = max(max_length, dfs(i, j, 2, 1))
                    max_length = max(max_length, dfs(i, j, 3, 1))
        return max_length
