    def distanceSum(self, m: int, n: int, k: int) -> int:
        MOD = 10**9 + 7

        def factorial(n):
            if n == 0:
                return 1
            return n * factorial(n - 1) % MOD

        def combination(n, r):
            return factorial(n) // (factorial(r) * factorial(n - r)) % MOD

        def sum_of_manhattan_distances(m, n, k):
