            for i in range(m):
                for j in range(n):
                    for x in range(m):
                        for y in range(n):
                            if (x, y) != (i, j):
                                total = (total + abs(i - x) + abs(j - y)) % MOD
            return total

        return sum_of_manhattan_distances(m, n, k) % MOD
