        def count_pairs(m, n, k):
            if k == 0:
                return 0
            if k == 1:
                return m * n

            # Calculate the number of ways to place k pieces in an m x n grid
            total_ways = 0
            for i in range(1, m + 1):
                for j in range(1, n + 1):
                    remaining = k - 1
                    ways = 0
                    for x in range(1, m + 1):
                        for y in range(1, n + 1):
                            if (x, y) != (i, j):
                                ways += 1
                    total_ways = (total_ways + ways * (remaining + 1)) % MOD

