class Solution:
    def solveQueries(self, nums: List[int], queries: List[int]) -> List[int]:
        n = len(nums)
        min_distances = [-1] * n
        min_index = -1
        min_distance = float('inf')

        for i in range(n):
            if nums[i] == nums[queries[0]]:
                if min_index == -1:
                    min_index = i
                    min_distance = 0
                else:
                    min_distance = min(min_distance, abs(i - min_index))

        for i in range(n):
            if nums[i] == nums[queries[0]]:
                min_distances[i] = min_distance

        for i in range(n):
            if nums[i] == nums[queries[1]]:
                if min_index == -1:
                    min_index = i
                    min_distance = 0
                else:
                    min_distance = min(min_distance, abs(i - min_index))

        for i in range(n):
            if nums[i] == nums[queries[1]]:
                min_distances[i] = min(min_distances[i], min_distance)

        for i in range(n):
            if nums[i] == nums[queries[2]]:
                if min_index == -1:
                    min_index = i
                    min_distance = 0
                else:
                    min_distance = min(min_distance, abs(i - min_index))

        for i in range(n):
            if nums[i] == nums[queries[2]]:
                min_distances[i] = min(min_distances[i], min_distance)

        return min_distances
