class Solution:
    def solveQueries(self, nums: List[int], queries: List[int]) -> List[int]:
        n = len(nums)
        min_distances = [-1] * n
        last_index = [-1] * n

        for i in range(n):
            if last_index[nums[i]] != -1:
                min_distances[i] = i - last_index[nums[i]]
            last_index[nums[i]] = i

        for i in range(n):
            if last_index[nums[i]] != -1:
                min_distances[i] = min(min_distances[i], n - last_index[nums[i]] + i)
            last_index[nums[i]] = i

        for i in range(n):
            if last_index[nums[i]] != -1:
                min_distances[i] = min(min_distances[i], last_index[nums[i]] - i)
            last_index[nums[i]] = i

        return [min_distances[i] for i in queries]
