class Solution:
    def maxDistance(self, s: str, k: int) -> int:
        """
        :type s: str
        :type k: int
        :rtype: int
        """
        n = len(s)
        dp = [[[-1] * 4 for _ in range(4)] for _ in range(n + 1)]

        def dfs(i, prev, k):
            if i == n:
                return 0
            if dp[i][prev][k] != -1:
                return dp[i][prev][k]

            max_dist = 0
            for d in range(4):
                if k > 0 and d != prev:
                    max_dist = max(max_dist, 1 + dfs(i + 1, d, k - 1))
                else:
                    max_dist = max(max_dist, 1 + dfs(i + 1, d, k))

            dp[i][prev][k] = max_dist
            return dp[i][prev][k]

        return dfs(0, -1, k)
