class Solution:
    def maxDistance(self, s: str, k: int) -> int:
        """
        :type s: str
        :type k: int
        :rtype: int
        """
        n = len(s)
        dp = [[[-1] * (k + 1) for _ in range(n + 1)] for _ in range(n + 1)]

        def dfs(i, j, k):
            if i == n:
                return 0
            if dp[i][j][k] != -1:
                return dp[i][j][k]

            # Stay at the same position
            stay = dfs(i + 1, j, k)

            # Move north
            if j > 0:
                move_north = dfs(i + 1, j - 1, k) + 1
            else:
                move_north = dfs(i + 1, j, k) + 1 if k > 0 else float('-inf')

            # Move south
            if j < n - 1:
                move_south = dfs(i + 1, j + 1, k) + 1
            else:
                move_south = dfs(i + 1, j, k) + 1 if k > 0 else float('-inf')

            # Move east
            if i < n - 1:
                move_east = dfs(i + 1, j, k) + 1
            else:
                move_east = dfs(i + 1, j, k) + 1 if k > 0 else float('-inf')

            # Move west
            if i > 0:
                move_west = dfs(i + 1, j, k) + 1
            else:
                move_west = dfs(i + 1, j, k) + 1 if k > 0 else float('-inf')

            dp[i][j][k] = max(stay, move_north, move_south, move_east, move_west)
            return dp[i][j][k]

        return dfs(0, 0, k)
