        def dfs(i, k, x, y):
            if i == n:
                return 0

            if dp[i][k][x] != -1:
                return dp[i][k][x]

            max_dist = 0

            if k > 0:
                max_dist = max(max_dist, dfs(i + 1, k - 1, x, y))

            if x > 0:
                max_dist = max(max_dist, dfs(i + 1, k, x - 1, y) + 1)
            elif x < 0:
                max_dist = max(max_dist, dfs(i + 1, k, x + 1, y) + 1)

            if y > 0:
                max_dist = max(max_dist, dfs(i + 1, k, x, y - 1) + 1)
            elif y < 0:
                max_dist = max(max_dist, dfs(i + 1, k, x, y + 1) + 1)

            dp[i][k][x] = max_dist
            return dp[i][k][x]

        return dfs(0, k, 0, 0)
