class Solution:
    def maxDistance(self, s: str, k: int) -> int:
        """
        :type s: str
        :type k: int
        :rtype: int
        """
        n = len(s)
        dp = [[[-1] * (k + 1) for _ in range(n)] for _ in range(n)]

        def dfs(i, j, k):
            if i == n:
                return 0
            if dp[i][j][k] != -1:
                return dp[i][j][k]

            # Move north
            if j > 0 and s[i] == 'N':
                dp[i][j][k] = max(dp[i][j][k], dfs(i + 1, j - 1, k))
            # Move south
            if j < n - 1 and s[i] == 'S':
                dp[i][j][k] = max(dp[i][j][k], dfs(i + 1, j + 1, k))
            # Move east
            if i < n - 1 and s[i] == 'E':
                dp[i][j][k] = max(dp[i][j][k], dfs(i + 1, j, k))
            # Move west
            if i > 0 and s[i] == 'W':
                dp[i][j][k] = max(dp[i][j][k], dfs(i - 1, j, k))

            # Change direction
            if k > 0:
                dp[i][j][k] = max(dp[i][j][k], dfs(i + 1, j, k - 1))

            dp[i][j][k] += abs(i - j)
