class Solution:
    def findMaxSum(self, nums1: List[int], nums2: List[int], k: int) -> List[int]:
        """
        :type nums1: List[int]
        :type nums2: List[int]
        :type k: int
        :rtype: List[int]
        """
        n = len(nums1)
        max_heap = []
        for i in range(n):
            heapq.heappush(max_heap, (-nums1[i], i))

        res = [0] * n
        for i in range(n):
            if not max_heap:
                break
            _, idx = heapq.heappop(max_heap)
            if k > 0:
                res[idx] = -nums1[idx]
                k -= 1
            else:
                break
            for j in range(idx + 1, n):
                if nums2[j] > nums2[idx]:
                    heapq.heappush(max_heap, (-nums1[j], j))
                    res[j] = -nums1[j]
                    k -= 1
                    break
            if k == 0:
                break

        return res
