class Solution:
    def findMaxSum(self, nums1: List[int], nums2: List[int], k: int) -> List[int]:
        """
        :type nums1: List[int]
        :type nums2: List[int]
        :type k: int
        :rtype: List[int]
        """
        n = len(nums1)
        result = [0] * n

        for i in range(n):
            max_indices = []
            for j in range(n):
                if nums1[j] < nums1[i] and j not in max_indices:
                    max_indices.append(j)

            if len(max_indices) > k:
                max_indices.sort(key=lambda x: -nums2[x])
