class Solution:
    def separateSquares(self, squares: List[List[int]]) -> float:
        """
        :type squares: List[List[int]]
        :rtype: float
        """
        squares.sort(key=lambda x: (x[0], x[1]))
        total_area = 0
        for x, y, l in squares:
            total_area += l * l
        total_area /= 2
        left, right = 0, 10**9
        while left < right:
            mid = (left + right) / 2
            above = 0
            below = 0
            for x, y, l in squares:
                if y > mid:
                    above += l * l
                else:
                    below += l * l
            if above == below:
                return mid
            elif above > below:
                right = mid
            else:
                left = mid + 1
        return left
