class Solution:
    def generateString(self, str1: str, str2: str) -> str:
        """
        Returns the lexicographically smallest possible string that can be generated by str1 and str2.
        If no string can be generated, returns an empty string.
        """
        n = len(str1)
        m = len(str2)
        result = [''] * (n + m - 1)

        i = 0
        j = 0

        while i < n and j < m:
            if str1[i] == 'T':
                if result[i] == '':
                    result[i] = str2
                elif result[i] != str2:
                    return ""
            elif str1[i] == 'F':
                if result[i] == '':
                    result[i] = str2
                elif result[i] == str2:
                    return ""
            i += 1
            j += 1

        while i < n:
            if result[i] == '':
                result[i] = str1[i]
            elif result[i] != str1[i]:
                return ""
            i += 1

        while j < m:
            if result[i] == '':
                result[i] = str2[j]
            elif result[i] == str2[j]:
                return ""
            j += 1

        return ''.join(result)
