class Solution:
    def maxDistance(self, side: int, points: List[List[int]], k: int) -> int:
        """
        :type side: int
        :type points: List[List[int]]
        :type k: int
        :rtype: int
        """
        points.sort(key=lambda x: (x[0], x[1]))
        return max(self.maxDistanceHelper(points, k, 0, side, 0, side))

    def maxDistanceHelper(self, points, k, i, side, x1, x2):
        if k == 0:
            return 0
