class Solution:
    def permute(self, n: int, k: int) -> List[int]:
        """
        Returns the k-th alternating permutation sorted in lexicographical order.
        If there are fewer than k valid alternating permutations, returns an empty list.
        """
        def is_valid_permutation(perm):
            """
            Checks if a permutation is valid.
            """
            for i in range(n - 1):
                if (perm[i] % 2 == perm[i + 1] % 2):
                    return False
            return True

        def generate_permutations(n, k):
            """
            Generates all alternating permutations of the first n positive integers.
            """
            def backtrack(perm, used):
                if len(perm) == n:
                    if is_valid_permutation(perm):
                        yield perm
                    return

                for i in range(1, n + 1):
                    if not used[i - 1]:
                        used[i - 1] = True
                        yield from backtrack(perm + [i], used)
                        used[i - 1] = False

            used = [False] * n
            for perm in backtrack([], used):
                yield perm

        # Generate all alternating permutations
        all_permutations = list(generate_permutations(n, k))

        # Return the k-th permutation (0-indexed)
        return all_permutations[k - 1] if k <= len(all_permutations) else []
