class Solution:
    def longestCommonPrefix(self, words: List[str], k: int) -> List[int]:
        def common_prefix(a, b):
            i = 0
            while i < len(a) and i < len(b) and a[i] == b[i]:
                i += 1
            return a[:i]

        def dfs(i, prefix, visited):
            if len(visited) == k:
                return [len(prefix)]
            if i == len(words):
                return []
            if i in visited:
                return dfs(i + 1, prefix, visited)
            result = []
            for j in range(i + 1, len(words)):
                if j not in visited:
                    new_prefix = common_prefix(words[i], words[j])
                    if new_prefix:
                        visited.add(j)
                        result.extend(dfs(j, new_prefix, visited))
                        visited.remove(j)
            return result

        return dfs(0, "", set())
