    def longestCommonPrefix(self, words: List[str], k: int) -> List[int]:
        def common_prefix(a, b):
            """Return the longest common prefix of two strings."""
            i = 0
            while i < len(a) and i < len(b) and a[i] == b[i]:
                i += 1
            return a[:i]

        def dfs(i, visited, prefix):
            """Perform a depth-first search to find the longest common prefix."""
            if len(visited) == k:
                return prefix
            if i == len(words):
                return ""
            max_prefix = ""
            for j in range(len(words)):
                if j not in visited:
                    new_prefix = common_prefix(prefix, words[j])
                    max_prefix = max(max_prefix, dfs(i + 1, visited | {j}, new_prefix))
            return max_prefix

        return [dfs(0, {i}, words[i]) for i in range(len(words))]

class Solution:
    def longestCommonPrefix(self, words: List[str], k: int) -> List[int]:
        def lcp(a, b):
            """Returns the longest common prefix of two strings."""
            return a[:min(len(a), len(b))].find(b[:min(len(a), len(b))])

        def dfs(i, path, visited):
            """Recursively explores all possible combinations of words."""
            if len(path) == k:
                return
            for j in range(i + 1, len(words)):
                if j not in visited:
                    visited.add(j)
                    path.append(words[j])
                    dfs(j, path, visited)
                    path.pop()
                    visited.remove(j)

        ans = [0] * len(words)
        for i in range(len(words)):
            visited = set()
            visited.add(i)
            dfs(i, [words[i]], visited)
            for j in range(len(words)):
                if j not in visited:
                    ans[j] = max(ans[j], lcp(words[i], words[j]))
        return ans

class Solution:
    def longestCommonPrefix(self, words: List[str], k: int) -> List[int]:
        def longest_common_prefix(words, i):
            if len(words) < k:
                return 0
            if i == len(words):
                return 0
            prefix = words[i]
            for j in range(i + 1, len(words)):
                if not words[j].startswith(prefix):
                    return longest_common_prefix(words, i + 1)
            return len(prefix)

        return [longest_common_prefix(words, i) for i in range(len(words))]

