class Solution:
    def maxSubarrays(self, n: int, conflictingPairs: List[List[int]]) -> int:
        """
        :type n: int
        :type conflictingPairs: List[List[int]]
        :rtype: int
        """
        # Create a set of all pairs to quickly check for conflicts
        pairs = set(tuple(sorted(pair)) for pair in conflictingPairs)

        # Create a list of all possible subarrays
        subarrays = []
        for i in range(n):
            for j in range(i + 1, n + 1):
                subarrays.append(tuple(range(i, j)))

        # Create a dictionary to store the number of subarrays that do not contain each pair
        subarrays_without_pairs = {pair: 0 for pair in pairs}

        # Iterate over all subarrays and count the number of subarrays that do not contain each pair
        for subarray in subarrays:
            contains_pair = False
            for pair in pairs:
                if pair[0] in subarray and pair[1] in subarray:
                    contains_pair = True
                    break
            if not contains_pair:
                subarrays_without_pairs[tuple(subarray)] += 1

        # Find the maximum number of subarrays that do not contain any pair
        max_subarrays = 0
        for subarray in subarrays:
            if subarray not in conflictingPairs:
                max_subarrays = max(max_subarrays, subarrays_without_pairs[tuple(subarray)])

        return max_subarrays
