class Solution:
    def totalNumbers(self, digits: List[int]) -> int:
        """
        Returns the number of distinct three-digit even numbers that can be formed using the given digits.
        Each digit can only be used once per number, and there may not be leading zeros.

        :param digits: List[int] - A list of digits.
        :return: int - The number of distinct three-digit even numbers.
        """
        def backtrack(index, current_number):
            if len(current_number) == 3:
                if int(current_number) % 2 == 0:
                    result.add(int(current_number))
                return

            for i in range(index, len(digits)):
                if i > index and digits[i] == digits[i-1]:
                    continue
                backtrack(i+1, current_number + str(digits[i]))

        result = set()
        backtrack(0, "")
        return len(result)
