            for j in range(i + 1, len(properties)):
                if len(set(properties[i]).intersection(set(properties[j]))) >= k:
                    graph[i].append(j)
                    graph[j].append(i)

        visited = set()
        def dfs(node):
            if node in visited:
                return
            visited.add(node)
            for neighbor in graph[node]:
                dfs(neighbor)

        count = 0
        for i in range(len(properties)):
            if i not in visited:
                dfs(i)
                count += 1

        return count

class Solution:
    def numberOfComponents(self, properties: List[List[int]], k: int) -> int:
        """
        :type properties: List[List[int]]
        :type k: int
        :rtype: int
        """
        graph = {}
        for i in range(len(properties)):
            for j in range(i + 1, len(properties)):
                if self.intersect(properties[i], properties[j]) >= k:
                    if i not in graph:
                        graph[i] = set()
                    if j not in graph:
                        graph[j] = set()
                    graph[i].add(j)
                    graph[j].add(i)

        visited = set()
        count = 0
        for node in graph:
            if node not in visited:
                self.dfs(graph, node, visited)
                count += 1
        return count

    def intersect(self, a, b):
        """
        :type a: List[int]
        :type b: List[int]
        :rtype: int
        """
        return len(set(a).intersection(set(b)))

    def dfs(self, graph, node, visited):
        """
        :type graph: dict
        :type node: int
        :type visited: set
        """
        if node in visited:
            return
        visited.add(node)
        for neighbor in graph[node]:
            self.dfs(graph, neighbor, visited)

