from collections import defaultdict
from math import gcd

def is_prime(n):
    """Check if a number is prime."""
    if n <= 1:
        return False
    if n <= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

def find_divisors(n):
    """Find all divisors of a number."""
    divisors = []
    for i in range(1, int(n**0.5) + 1):
        if n % i == 0:
            divisors.append(i)
            if i != n // i:
                divisors.append(n // i)
    return divisors

def mod_inverse(a, m):
    """Find the modular multiplicative inverse of a modulo m."""
    for x in range(1, m):
        if (a * x) % m == 1:
            return x
    return None

def euler_totient(n, p):
    """Calculate Euler's Totient function for a number n modulo a prime p."""
    totient = n
    for d in find_divisors(n):
        if d == 1 or d == n:
            continue
        if gcd(d, p) == 1:
            totient -= totient // d
    return totient

def count_connected_graphs(N, P):
    """Count the number of connected graphs with N vertices and M edges modulo P."""
    if not is_prime(P):
        raise ValueError("P must be a prime number.")

    def dfs(graph, start, visited, path):
        """Depth-First Search to find connected components."""
        visited[start] = True
        path.append(start)
        for neighbor in graph[start]:
            if not visited[neighbor]:
                dfs(graph, neighbor, visited, path)

    def count_connected_components(N, P):
        """Count the number of connected components in a graph."""
        graph = defaultdict(list)
        for i in range(1, N + 1):
            for j in range(i + 1, N + 1):
                graph[i].append(j)
                graph[j].append(i)
        visited = [False] * (N + 1)
        components = 0
        for i in range(1, N + 1):
            if not visited[i]:
                path = []
                dfs(graph, i, visited, path)
                if len(path) > 1:
                    components += 1
        return components

    def count_even_odd_distances(N, P):
        """Count the number of vertices with even and odd distances from vertex 1."""
        graph = defaultdict(list)
        for i in range(1, N + 1):
            for j in range(i + 1, N + 1):
                graph[i].append(j)
                graph[j].append(i)
        visited = [False] * (N + 1)
        even_count = 0
        odd_count = 0
        for i in range(1, N + 1):
            if not visited[i]:
                path = []
                dfs(graph, i, visited, path)
                if len(path) > 1:
                    for j in path:
                        if path.index(j) % 2 == 0:
                            even_count += 1
                        else:
                            odd_count += 1
        return even_count, odd_count

    def count_valid_graphs(N, P):
        """Count the number of valid graphs modulo P."""
        even_count, odd_count = count_even_odd_distances(N, P)
        if even_count != odd_count:
            return 0
        return euler_totient(N, P)

    M = (N * (N - 1)) // 2
    return [count_valid_graphs(N, P) % P for _ in range(M)]
