import math
from collections import defaultdict

def prime_factors(n):
    """Returns a list of prime factors of n."""
    i = 2
    factors = []
    while i * i <= n:
        if n % i:
            i += 1
        else:
            n //= i
            factors.append(i)
    if n > 1:
        factors.append(n)
    return factors

def is_prime(n):
    """Returns True if n is a prime number, False otherwise."""
    if n <= 1:
        return False
    if n <= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

def euler_totient(n):
    """Returns the Euler's totient function value of n."""
    if n == 1:
        return 1
    factors = prime_factors(n)
    result = n
    for p in set(factors):
        result *= (1 - 1 / p)
    return int(result)

def count_connected_graphs(N, M, P):
    """Returns the number of connected graphs with N vertices and M edges modulo P."""
    if not is_prime(P):
        raise ValueError("P must be a prime number.")

    def dfs(graph, visited, start):
        """Performs a depth-first search to count connected components."""
        visited[start] = True
        for neighbor in graph[start]:
            if not visited[neighbor]:
                dfs(graph, visited, neighbor)

    def count_connected_components(N, M, P):
        """Counts the number of connected components in a graph with N vertices and M edges."""
        graph = defaultdict(list)
        for i in range(1, N + 1):
            for j in range(i + 1, N + 1):
                graph[i].append(j)
                graph[j].append(i)

        visited = [False] * (N + 1)
        count = 0
        for i in range(1, N + 1):
            if not visited[i]:
                dfs(graph, visited, i)
                count += 1
        return count

    def count_even_odd_distance_vertices(N, M, P):
        """Counts the number of vertices with even and odd shortest distance from vertex 1."""
        graph = defaultdict(list)
        for i in range(1, N + 1):
            for j in range(i + 1, N + 1):
                graph[i].append(j)
                graph[j].append(i)

        visited = [False] * (N + 1)
        distances = [0] * (N + 1)
        distances[1] = 0

        def bfs(graph, start, distances):
            """Performs a breadth-first search to find shortest distances from vertex 1."""
            queue = [start]
            visited[start] = True
            while queue:
                current = queue.pop(0)
                for neighbor in graph[current]:
                    if not visited[neighbor]:
                        distances[neighbor] = distances[current] + 1
                        visited[neighbor] = True
                        queue.append(neighbor)

        bfs(graph, 1, distances)

        even_count = 0
        odd_count = 0
        for i in range(1, N + 1):
            if distances[i] % 2 == 0:
                even_count += 1
            else:
                odd_count += 1

        return even_count, odd_count

    even_count, odd_count = count_even_odd_distance_vertices(N, M, P)
    if even_count != odd_count:
        return 0

    connected_components = count_connected_components(N, M, P)
    return (euler_totient(connected_components) * (P - 1)) % P
