def find_subgrid(S, T):
    """
    Finds the top-left corner of the subgrid in S that matches the subgrid T.

    Parameters:
    S (str): The N x N grid represented as a string.
    T (str): The M x M grid represented as a string.

    Returns:
    tuple: A tuple (a, b) representing the top-left corner of the subgrid in S.
    """
    N = len(S) // N
    M = len(T) // M

    for a in range(N - M + 1):
        for b in range(N - M + 1):
            match = True
            for i in range(M):
                for j in range(M):
                    if S[a * N + b * N + i * N + j] != T[i * M + j]:
                        match = False
                        break
                if not match:
                    break
            if match:
                return (a + 1, b + 1)
    return None

