def majority_element(arr):
    """
    Returns the majority element in the array.
    """
    count = {}
    for num in arr:
        if num in count:
            count[num] += 1
        else:
            count[num] = 1
    return max(count, key=count.get)

def majority_element_group(arr):
    """
    Returns the majority element in each group of 3 elements.
    """
    return [majority_element(arr[i:i+3]) for i in range(0, len(arr), 3)]

