    return N, C

def is_palindrome(s):
    """
    Checks if a string s is a palindrome.
    """
    return s == s[::-1]

def shortest_palindrome_path(N, C):
    """
    Computes the shortest palindrome path from vertex i to vertex j.
    """
    from collections import defaultdict
    from heapq import heappush, heappop

    graph = defaultdict(list)
    for i in range(N):
        for j in range(N):
            if C[i][j] != '-':
                graph[i].append((j, C[i][j]))

    def dijkstra(start, end):
        """
        Computes the shortest path from vertex start to vertex end using Dijkstra's algorithm.
        """
        distances = {i: float('inf') for i in range(N)}
        distances[start] = 0
        pq = [(0, start)]
        while pq:
            current_distance, current_vertex = heappop(pq)
            if current_distance > distances[current_vertex]:
                continue
            for neighbor, label in graph[current_vertex]:
                distance = current_distance + (1 if label == label[::-1] else 0)
                if distance < distances[neighbor]:
                    distances[neighbor] = distance
                    heappush(pq, (distance, neighbor))
        return distances[end]

    shortest_paths = {i: float('inf') for i in range(N)}
    for i in range(N):
        for j in range(N):
            if i != j:
                shortest_paths[(i, j)] = dijkstra(i, j)
                shortest_paths[(j, i)] = dijkstra(j, i)

