    import sys
    input = sys.stdin.read
    data = input().split()
    T = int(data[0])
    results = []
    for i in range(T):
        N, K = map(int, data[1:3])
        cakes = []
        for j in range(3, 3 + 3 * N):
            X, Y, Z = map(int, data[j:j + 3])
            cakes.append((X, Y, Z))
        results.append(max_price(cakes, K))
