from functools import reduce

def count_distinct_sequences(N, s):
    """
    Given a positive integer N and a length-N string s_0s_1\ldots s_{N-1} consisting only of 0 and 1,
    return the number of distinct sequences (d_0, d_1, \ldots, d_N) that can be obtained by assigning
    directions to each edge of the graph G' as described.
    """
    graph = defaultdict(list)
    in_degree = [0] * (N + 1)

    for i in range(N):
        if s[i] == '1':
            graph[i].append((i + 1) % N)
            graph[(i + 1) % N].append(i)
            in_degree[i] += 1
            in_degree[(i + 1) % N] += 1

    for i in range(N):
        if in_degree[i] == 0:
            queue = deque([i])
            while queue:
                node = queue.popleft()
                for neighbor in graph[node]:
                    in_degree[neighbor] -= 1
                    if in_degree[neighbor] == 0:
                        queue.append(neighbor)

    return reduce(lambda x, y: x * y % 998244353, in_degree, 1)
