    neg_A = sum(1 for x in A if x < 0)
    neg_B = sum(1 for x in B if x < 0)

    # If there are more negative elements in A than in B, it's impossible
    if neg_A > neg_B:
        print("No")
        return

    # If there are more negative elements in B than in A, we need to replace
    # the negative elements in A with positive integers to balance the sums
    if neg_B > neg_A:
        for i in range(len(A)):
            if A[i] < 0:
                A[i] = 0
        for i in range(len(B)):
            if B[i] < 0:
                B[i] = 0
        if sum(A) == sum(B):
            print("Yes")
        else:
            print("No")
        return

    # If there are the same number of negative elements in A and B, we can
    # balance the sums by replacing the negative elements in A with positive integers
    for i in range(len(A)):
        if A[i] < 0:
            A[i] = 0
    if sum(A) == sum(B):
        print("Yes")
    else:
        print("No")
