def solve_problem():
    """
    Solves the problem by generating a valid placement of R red and B blue pieces on a 10^9x10^9 board.
    Returns a string representation of the placement if it exists, otherwise returns "No".
    """
    import random

    def is_valid_placement(board, reds, blues):
        """
        Checks if the current placement of reds and blues is valid.
        """
        for i in range(len(reds)):
            for j in range(i + 1, len(reds)):
                if abs(reds[i][0] - blues[j][0]) + abs(reds[i][1] - blues[j][1]) == 1:
                    return False
        return True

    def place_piece(board, reds, blues, piece_type, row, col):
        """
        Places a piece of the given type at the specified row and column.
        """
        if piece_type == 'R':
            reds.append((row, col))
        else:
            blues.append((row, col))
        board[row][col] = piece_type

    def remove_piece(board, reds, blues, piece_type, row, col):
        """
        Removes a piece of the given type from the specified row and column.
        """
        if piece_type == 'R':
            reds = [p for p in reds if p != (row, col)]
        else:
            blues = [p for p in blues if p != (row, col)]
        board[row][col] = '.'

    def backtrack(board, reds, blues, red_count, blue_count, row, col):
        """
        Backtracks to find a valid placement of reds and blues.
        """
        if red_count == 0 and blue_count == 0:
            if is_valid_placement(board, reds, blues):
                return True
            return False

        if row == 10**9:
            return False

        if col == 10**9:
            return backtrack(board, reds, blues, red_count, blue_count, row + 1, 0)

        if board[row][col] != '.':
            return backtrack(board, reds, blues, red_count, blue_count, row, col + 1)

        if red_count > 0:
            place_piece(board, reds, blues, 'R', row, col)
            if backtrack(board, reds, blues, red_count - 1, blue_count, row, col + 1):
                return True
            remove_piece(board, reds, blues, 'R', row, col)

        if blue_count > 0:
            place_piece(board, reds, blues, 'B', row, col)
            if backtrack(board, reds, blues, red_count, blue_count - 1, row, col + 1):
                return True
            remove_piece(board, reds, blues, 'B', row, col)

        return backtrack(board, reds, blues, red_count, blue_count, row, col + 1)

    def generate_board():
        """
        Generates a random board with R red and B blue pieces.
        """
        board = [['.' for _ in range(10**9)] for _ in range(10**9)]
        reds = []
        blues = []
        for _ in range(random.randint(1, 2 * 10**5)):
            if random.random() < 0.5:
                reds.append((random.randint(0, 10**9 - 1), random.randint(0, 10**9 - 1)))
            else:
                blues.append((random.randint(0, 10**9 - 1), random.randint(0, 10**9 - 1)))
        return board, reds, blues

    def print_placement(reds, blues):
        """
        Prints the placement of reds and blues in a readable format.
        """
        for r in range(10**9):
            for c in range(10**9):
                if (r, c) in reds:
                    print('R', end=' ')
                elif (r, c) in blues:
                    print('B', end=' ')
                else:
                    print('.', end=' ')
            print()

    T = int(input())
    for _ in range(T):
        R, B = map(int, input().split())
        board, reds, blues = generate_board()
        if not backtrack(board, reds, blues, R, B, 0, 0):
            print("No")
        else:
            print("Yes")
            print_placement(reds, blues)
