def solve_problem():
    """
    Solves the problem by generating a valid placement of R red and B blue pieces
    on a 10^9 x 10^9 board under the given constraints.
    """
    import random

    def is_valid_placement(placement):
        """
        Checks if the given placement of R red and B blue pieces is valid.
        """
        n = 10**9
        red_positions = {i: set() for i in range(n)}
        blue_positions = {i: set() for i in range(n)}

        for i, (piece, r, c) in enumerate(placement):
            if piece == 'R':
                if r + 1 < n and (r + 1, c) in red_positions:
                    return False
                if r - 1 >= 0 and (r - 1, c) in red_positions:
                    return False
                if c + 1 < n and (r, c + 1) in red_positions:
                    return False
                if c - 1 >= 0 and (r, c - 1) in red_positions:
                    return False
                red_positions[r].add(c)
            else:
                if r + 1 < n and (r + 1, c + 1) in blue_positions:
                    return False
                if r + 1 < n and (r + 1, c - 1) in blue_positions:
                    return False
                if r - 1 >= 0 and (r - 1, c + 1) in blue_positions:
                    return False
                if r - 1 >= 0 and (r - 1, c - 1) in blue_positions:
                    return False
                blue_positions[r].add(c)

        return True

    def generate_placement(R, B):
        """
        Generates a valid placement of R red and B blue pieces on a 10^9 x 10^9 board.
        """
        n = 10**9
        red_positions = {i: set() for i in range(n)}
        blue_positions = {i: set() for i in range(n)}

        for i in range(R):
            r = random.randint(0, n - 1)
            c = random.randint(0, n - 1)
            while (r, c) in red_positions or (r, c) in blue_positions:
                r = random.randint(0, n - 1)
                c = random.randint(0, n - 1)
            red_positions[r].add(c)

        for i in range(B):
            r = random.randint(0, n - 1)
            c = random.randint(0, n - 1)
            while (r, c) in red_positions or (r, c) in blue_positions:
                r = random.randint(0, n - 1)
                c = random.randint(0, n - 1)
            blue_positions[r].add(c)

        return [(i % 2, r, c) for i, (r, c) in enumerate(red_positions.items())]

    for _ in range(int(input())):
        R, B = map(int, input().split())
        placement = generate_placement(R, B)
        if is_valid_placement(placement):
            print("Yes")
            for i, (piece, r, c) in enumerate(placement):
                print(f"{piece} {r} {c}")
        else:
            print("No")
