    graph = defaultdict(list)
    for i in range(2 * N):
        if S[i] == 'W':
            graph[i].append(i + 1)
        else:
            graph[i].append(i + 2)

    count = 0
    for i in range(2 * N):
        for j in range(i + 1, 2 * N):
            if S[i] == 'W' and S[j] == 'B':
                graph[i].append(j + 1)
                graph[j].append(i + 1)
                if is_strongly_connected(graph):
                    count = (count + 1) % 998244353
                graph[i].pop()
                graph[j].pop()

    return count

